const burger=document.querySelector(".burger-open"),menu=document.querySelector(".mobile-menu");burger.addEventListener("click",(function(){menu.classList.toggle("open")}));const btns=document.querySelectorAll(".starship__link"),about=document.querySelector(".tabs"),articles=document.querySelectorAll(".starship-rocket");about.addEventListener("click",(function(e){e.preventDefault();const t=e.target.dataset.id;if(t){btns.forEach((function(t){t.classList.remove("active"),e.target.classList.add("active")})),articles.forEach((function(e){e.classList.remove("active")})),document.getElementById(t).classList.add("active")}}));